import React, {Component} from 'react';
import './Birds.css'


class Birds extends Component {

    state = {
        item: 0
    };

    clickResult(item) {
        this.props.answerData(item);
        this.setState({item: item.id})
    }

    render() {
        const {data, data2} = this.props;
        console.log(data2);
        console.log(this.state.item);
        return (
            <ul className="item-list list-group">
                {data.map((item) => (
                    <li className={`list-group-item ${data2 === undefined ? '' : data2 === this.state.item ? 'error' : ''}`}
                        key={item.id}
                        onClick={() => this.clickResult(item)}>
                      <span className={'li-btn'}>
                      </span>
                        {item.name}
                    </li>
                ))}
            </ul>
        );
    }
}

export default Birds;
