import React, {Component} from 'react';
import './Birds.css'


class Birds extends Component {

    state = {
        clazzTrue:'success',
        clazzFalse:'error'
    };

    clickResult(item) {
        this.props.answerData(item);
        const {data2} = this.props;
        if (item.id === data2) {
            this.setState({clazzTrue: 'success'})
        } else {
            this.setState({clazzFalse:'error'})
        }
    }

    render() {
        const {data, data2} = this.props;
        return (
            <ul className="item-list list-group">
                {data.map((item) => (
                    <li className={`list-group-item ${data2?this.state.clazzTrue:''}`} key={item.id}
                        onClick={() => this.clickResult(item)}>
                      <span className={'li-btn'}>
                      </span>
                        {item.name}
                    </li>
                ))}
            </ul>
        );
    }
}

export default Birds;
