import React, {Component} from 'react';
import './Birds.css'


class Birds extends Component {

    state = {
        item: ' error',
        done:false
    };
    coun = 5;


    clickResult(item, e) {
        if (this.props.index >= 0 && !this.props.btn) {
            this.setState({item: ' error'})
        }
        this.props.answerClick(item);
        if (e.target.innerText === this.props.data[this.props.randomBird][this.props.randomBird].name) {
            e.target.className += ' success';
            this.props.answerCount(this.coun);
            this.coun = 5;
            this.setState({done:true})
            if(this.props.error){
                this.setState({done:false})
            }
        } else {
            if(!this.state.done){
                e.target.className += this.state.item;
                this.count(e.target.className)
            }
        }

    }

    count(count) {
        if (count === 'list-group-item error') {
            this.coun--;
        }

    }

    render() {
        const data = this.props.data[this.props.randomBird];
        return (
            <ul className="item-list list-group">
                {data.map((item) => (
                    <li className={`list-group-item`}
                        key={item.id}
                        onClick={(e) => this.clickResult(item, e)}>
                      <span className={'li-btn'}>
                      </span>
                        {item.name}
                    </li>
                ))}
            </ul>
        );
    }
}

export default Birds;
