import React, {Component} from 'react';
import './Birds.css'
import {Howl} from "howler";
import lose from "../../audio/false.mp3";
import corect from '../../audio/true.mp3'


class Birds extends Component {
    coun = 5;


    clickResult(item, e) {
        const sound = new Howl({
            src: ['sound.mp3'],
            autoplay: false,
            loop: false,
            volume: 0.5,
        });
        this.props.answerClick(item);
        if (e.target.innerText === this.props.data[this.props.randomBird][this.props.randomBird].name) {
            e.target.className += ' success';
            this.props.answerCount(this.coun);
            this.coun = 5;
            sound.play(corect)
        } else {
            if (!this.props.error) {
                console.log(Howl.src)
                sound.play();
                e.target.className += ' error';
                this.count(e.target.className)
            }
        }

    }

    count(count) {
        if (count === 'list-group-item error') {
            this.coun--;
        }


    }

    render() {
        const data = this.props.data[this.props.randomBird];
        return (
            <ul className="item-list list-group">
                {data.map((item) => (
                    <li className={`list-group-item`}
                        key={item.id}
                        onClick={(e) => this.clickResult(item, e)}>
                      <span className={'li-btn'}>
                      </span>
                        {item.name}
                    </li>
                ))}
            </ul>
        );
    }
}

export default Birds;
