import React, {Component} from 'react';
import './Birds.css'


class Birds extends Component {

    state = {
        clazz:'',
    };

    clickResult(item) {
        this.props.answerData(item);
        const {data2} = this.props;
       this.setState({clazz:item.id===data2? 'success': 'error'})
    }

    render() {
        const {data, data2} = this.props;
        return (
            <ul className="item-list list-group">
                {data.map((item) => (
                    <li className={`list-group-item ${this.state.clazz}`} key={item.id}
                        onClick={() => this.clickResult(item)}>
                      <span className={'li-btn'}>
                      </span>
                        {item.name}
                    </li>
                ))}
            </ul>
        );
    }
}

export default Birds;
