import React, {Component} from 'react';
import Header from "../Header/Header";
import Birds from "../Birds/Birds";
import Answer from "../Answer/Answer";
import Questions from "../Questions/Questions";
import birdsData from '../birdsData'
import PreLoader from "../PreLoader/PreLoader";
import './App.css'


class App extends Component {
    state = {
        data: birdsData,
        answer: false,
        true: false,
        data2: [],
        count: 0,
        loader: true,
        btn:false,
        randomBird:Math.floor(Math.random() * 5),
        index:0
    };
    count=0;

    componentDidMount() {
        const data2 = this.state.data[this.state.randomBird][this.state.randomBird];
        this.setState({data2: data2});
        console.log(data2)
    }

    answerCount(count) {
        this.setState({count});
        this.setState({btn: true})
    }
    answerClick(item){
        this.setState({answer:item})
    }

    Loader() {
        this.setState({loader: false})
    }
    onSort(){
        this.count ++;
        this.setState({randomBird:Math.floor(Math.random() * 5)});
        [...document.querySelectorAll(".list-group-item")].map(item => {
            item.classList.remove("success");
            item.classList.remove("error");
            return item;
        });
        this.setState({
            btn:false,
            answer:false,
            index:this.count
        });
        this.setState({data2: this.state.data[this.state.randomBird][this.state.randomBird]})
    }

    render() {
        return (
            <div className="container">
                {this.state.loader &&
                <PreLoader onLoader={this.Loader.bind(this)}/>}
                {!this.state.loader &&
                <div>
                    <Header count={this.state.count} index={this.state.index}/>
                    <Questions
                        data={this.state.data[this.state.randomBird][this.state.randomBird]}
                        true2={this.state.true}
                    />
                    <div className="row mb-2">
                        <div className="col-md-6">
                            <Birds
                                data={this.state.data[this.state.randomBird]}
                                answerCount={this.answerCount.bind(this)}
                                data2={this.state.data2.name}
                                answerClick={this.answerClick.bind(this)}
                            />
                        </div>
                        <div className="col-md-6">
                            <Answer answer={this.state.answer}/>
                        </div>
                        <button className={`btn ${this.state.btn ? 'btn-success' : ''}`}
                                disabled={!this.state.btn}
                                onClick={this.onSort.bind(this)}
                        > Next Level
                        </button>

                    </div>

                </div>
                }

            </div>
        );
    }
}

export default App;
