import React, {Component} from 'react';
import './Birds.css'


class Birds extends Component {

    state = {
        item: ' error'
    };

    clickResult(item, e) {
        let count = 0;
        console.log(e.target.innerText);
        console.log(this.props.data2);
        this.props.answerData(item);
        if (e.target.innerText === this.props.data2) {
            e.target.className += ' success';
            this.setState({item: ''})
        } else {
            e.target.className += this.state.item;
        }
        if (e.target.className ==='list-group-item error' ){
            let count = 0;
            count --;
        }
        console.log(count)
        console.log(e.target.className)


    }

    render() {
        const {data} = this.props;
        console.log(this.state.item);
        return (
            <ul className="item-list list-group">
                {data.map((item) => (
                    <li className={`list-group-item`}
                        key={item.id}
                        onClick={(e) => this.clickResult(item, e)}>
                      <span className={'li-btn'}>
                      </span>
                        {item.name}
                    </li>
                ))}
            </ul>
        );
    }
}

export default Birds;
