import React, {Component} from 'react';
import Header from "../Header/Header";
import Birds from "../Birds/Birds";
import Answer from "../Answer/Answer";
import Questions from "../Questions/Questions";
import birdsData from '../birdsData'
import PreLoader from "../PreLoader/PreLoader";
import './App.css'


const App=()=>{
    // state = {
    //     data: birdsData,
    //     answer: false,
    //     true: false,
    //     data2: '',
    //     count: 0,
    //     loader: true,
    //     btn:false,
    // };
    const data = Math.floor(Math.random() * this.state.data.length);

    componentDidMount=()=> {
        const data2 = this.state.data[this.data][this.data];
        this.setState({data2: data2})
    };

    const answerCount=(count)=> {
        this.setState({count});
        this.setState({btn: true})
    };
   const answerClick=(item)=>{
        this.setState({answer:item})
    };

    const Loader=()=> {
        this.setState({loader: false})
    };

        return (
            <div className="container">
                {this.state.loader &&
                <PreLoader onLoader={Loader}/>}
                {!this.state.loader &&
                <div>
                    <Header count={this.state.count}/>
                    <Questions
                        data={this.state.data[this.data][this.data]}
                        true2={this.state.true}
                    />
                    <div className="row mb-2">
                        <div className="col-md-6">
                            <Birds
                                data={this.state.data[this.data]}
                                answerCount={answerCount.bind(this)}
                                data2={this.state.data2.name}
                                answerClick={answerClick.bind(this)}
                            />
                        </div>
                        <div className="col-md-6">
                            <Answer answer={this.state.answer}/>
                        </div>
                        <button className={`btn ${this.state.btn ? 'btn-success' : ''}`}
                                disabled={!this.state.btn}> Next Level
                        </button>

                    </div>

                </div>
                }

            </div>
        );
};

export default App;
