import React, {Component} from 'react';
import Header from "../Header/Header";
import Birds from "../Birds/Birds";
import Answer from "../Answer/Answer";
import Questions from "../Questions/Questions";
import birdsData from '../birdsData'
import './App.css'


class App extends Component {
    state = {
        data: birdsData,
        answer: false,
        clazzAns: '',
        true: false,
        data2: []
    };
    data = Math.floor(Math.random() * this.state.data.length);

    answerData(item) {
        const data2 = this.state.data[this.data][this.data];
        this.setState({answer: item});
        if (item.id === data2.id) {
            this.setState({
                clazzAns: true,
                true: item,
                data2:data2
            });

        }
    }

    render() {
        return (
            <div className="container">
                <Header/>
                <Questions
                    data={this.state.data[this.data][this.data]}
                    true2={this.state.true}
                />
                <div className="row mb-2">
                    <div className="col-md-6">
                        <Birds
                            data={this.state.data[this.data]}
                            answerData={this.answerData.bind(this)}
                            clazzAns={this.state.clazzAns}
                            data2={this.state.data2.id}
                        />
                    </div>
                    <div className="col-md-6">
                        <Answer answer={this.state.answer}/>
                    </div>
                    <button className={`btn ${this.state.clazzAns ? 'btn-success' : ''}`}> Next Level</button>

                </div>


            </div>
        );
    }
}

export default App;
